select  '    '+c.name + ' = models.' + (case lower(y.name)when 'varchar'then 'CharField(max_length='+cast(c.length as varchar)+')'when 'char'  then 'CharField(max_length='+cast(c.length as varchar)+')'when 'datetime' then 'DateTimeField()'when 'int' then 'IntegerField()'when 'smallint' then 'IntegerField()'else 'Not Defined'end ) modelname --y.name FieldType,c.name ColumnName,c.length Length,* from syscolumns c, sysobjects t, sys.types ywhere t.id = c.id and t.name = 'anggota'and c.xtype = y.system_type_id
